/*Table structure for table `tblRaceNews` */

DROP TABLE IF EXISTS `tblRaceNews`;

CREATE TABLE `tblRaceNews` (
  `fldNewsID` int(10) NOT NULL auto_increment,
  `fldNewsTitle` varchar(30) NOT NULL default '',
  `fldNews` text NOT NULL,
  `fldPoster` varchar(20) NOT NULL default '',
  `fldDate` date default NULL,
  PRIMARY KEY  (`fldNewsID`)
) ENGINE=MyISAM DEFAULT CHARSET=latin1;

/*Data for the table `tblRaceNews` */

insert  into `tblRaceNews`(`fldNewsID`,`fldNewsTitle`,`fldNews`,`fldPoster`,`fldDate`) values (1,'Welcome to TWRC!','On behalf of the whole TWRC team I welcome you all to the TWRC website! Here you will find all the latest info about the new and spectaculair Racing Cup.<BR><BR>TWRC is currently on all open test. If all goes well, the first racing cup period will be held on the 1st of november. Watch those zone messages in Trench Wars and stay tuned for more upcoming news. ','Rodge_Rabbit','2004-10-11'),(2,'Bot Test','The first huge bot test will be starting on the 17th of October, at 3 PM EST (8 PM GMT) time. Be sure to download the maps already, and start warming up your engines!','Rodge_Rabbit','2004-10-11'),(3,'Guide made by Nightattack','Want to develop your racetrack, but you have no clue how to actually start?<br> Check out the guide NightAttack has made, you can find it on <a href=http://www.trenchwars.org/twrc/guide/>http://www.trenchwars.org/twrc/guide/</a><br><br>\r\n<b>Flag Bug and TWRC status</b>\r\nI also want to update you on the current status of TWRC. We are a little blown down to the fact that because of the arena ignoring every flag pickups, the racedata isn\'t send trough. <br>\r\nWe are checking out the possibility if this is a setting. If it\'s not, then TWRC will probably not continue.<br><br>-Rodge','Rodge_Rabbit','2004-11-14');

